var stylus = require('../')
  , fs = require('fs')
  , path = 'testing/small.styl'
  , str = fs.readFileSync(path, 'utf8');

var start = new Date;

str = str.replace(/\s*,\s*/g, ', ');

stylus(str)
  .set('filename', path)
  .render(function(err, css){
    if (err) throw err;
    console.log(css);
    // console.log('%dms', new Date - start);
  });